mapper.select:
SELECT a.id, a.hosp_code, a.bfc_code, a.type_code, a.big_type_code, a.code, a.usual_name, a.good_name,a.good_name as itemName,'1' as itemCode, a.spec, a.prep_code, a.dosage, a.dosage_unit_code, a.out_unit_code, a.in_unit_code, a.rate_code, a.price, a.unit_code, a.last_buy_price, a.last_split_buy_price, a.avg_buy_price, a.avg_sell_price, a.split_ratio, a.split_unit_code, a.split_price, a.is_out, a.is_in, a.is_lvp, a.usage_code, a.insure_code, a.nation_code, a.drug_remark, a.drug_img_path, a.max_dosage, a.min_dosage, a.gender_code, a.min_age, a.max_age, a.ddd, a.durg_code, a.ph_code, a.antibacterial_code, a.is_skin, a.is_basic, a.is_prescription, a.basic_code, a.proton_pump, a.ndan, a.prod_code, a.usual_pym, a.usual_wbm, a.good_pym, a.good_wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time, (select t.name from base_dept t where t.id = l.biz_id and t.hosp_code = l.hosp_code)bizName, l.biz_id , l.batch_no,l.expiry_date,l.num stockNum,l.split_num, (select d.id from base_finance_classify d where a.bfc_code = d.code and a.hosp_code = d.hosp_code limit 1) bfcId, (select d.name from base_finance_classify d where a.bfc_code = d.code and a.hosp_code = d.hosp_code limit 1) bfcName FROM stro_stock k, stro_stock_detail l, base_drug a WHERE k.hosp_code = l.hosp_code AND k.biz_id = l.biz_id AND k.item_id = l.item_id AND l.item_id = a.id AND l.hosp_code = a.hosp_code AND k.item_code = '1' AND l.hosp_code = #{hospCode}
AND l.biz_id = #{bizId}
and (a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%'))
and l.num > 0

SELECT a.hosp_code, a.id, max(a.good_name) AS itemName, '1' AS itemCode, max(a.price) price, max(a.last_buy_price) last_buy_price, sum(l.num) stockNum, max(a.unit_code) unit_code, max(a.split_ratio) split_ratio, sum(l.split_num) split_num, max(a.split_unit_code) split_unit_code, max(a.last_split_buy_price) last_split_buy_price, max(a.split_price) split_price, max(a.spec) spec, (select bp.name from base_product bp where bp.code = max(a.prod_code) and bp.hosp_code = a.hosp_code) as prodName FROM stro_stock k, stro_stock_detail l, base_drug a WHERE k.hosp_code = l.hosp_code AND k.biz_id = l.biz_id AND k.item_id = l.item_id AND l.item_id = a.id AND l.hosp_code = a.hosp_code AND k.item_code = '1' AND l.hosp_code = #{hospCode}
AND l.biz_id = #{bizId}
and (a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%'))
and l.num > 0
GROUP BY a.hosp_code, a.id

select count(id)
from base_drug
where hosp_code = #{hospCode}

	and code = #{code}


	and id != #{id}

select id, hosp_code, bfc_code, type_code, big_type_code, code, usual_name, good_name, spec, prep_code, dosage, dosage_unit_code, out_unit_code, in_unit_code, rate_code, price, unit_code, last_buy_price, last_split_buy_price, avg_buy_price, avg_sell_price, split_ratio, split_unit_code, split_price, is_out, is_in, is_lvp, usage_code, insure_code, nation_code, drug_remark, drug_img_path, max_dosage, min_dosage, gender_code, min_age, max_age, ddd, durg_code, ph_code, antibacterial_code, is_skin, is_basic, basic_code, proton_pump, ndan, prod_code, usual_pym, usual_wbm, good_pym, good_wbm, is_valid, crte_id, crte_name, crte_time, (select count(0) from stro_stock e where e.item_code = '1' and e.item_id = base_drug.id ) as stockFlag from base_drug where hosp_code = #{hospCode} and
id in
#{id}
