mapper.select:
select a.id, a.hosp_code, a.nation_code, a.type_code, a.bfc_code, a.code, a.name, a.abbr, a.spec, a.price, a.one_price, a.two_price,a.nation_name, a.three_price, a.unit_code, a.intension, a.prompt, a.except, a.remark, a.is_out, a.is_in, a.is_cg, a.is_ms, a.is_supp_curtain, a.out_dept_code, a.in_dept_code, a.name_pym, a.name_wbm, a.abbr_pym, a.abbr_wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time,a.other_name,a.nation_name as gb_nation_name, (select count(0) from base_advice_detail d where d.type_code = '3' and d.item_code = a.code) as adviceFlag, b.name as outDeptName, a.reimbursement_ratio, c.name as inDeptName from base_item a LEFT JOIN base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code LEFT JOIN base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code
a.hosp_code = #{hospCode}
and a.id = #{id}

select
a.id, a.hosp_code, a.nation_code, a.type_code, a.bfc_code, a.code, a.name, a.abbr, a.spec, a.price, a.one_price,
a.two_price,
a.three_price, a.unit_code, a.intension, a.prompt, a.except, a.remark, a.is_out, a.is_in, a.is_cg, a.is_ms,
a.is_supp_curtain,a.other_name,
a.out_dept_code, a.in_dept_code, a.name_pym, a.name_wbm, a.abbr_pym, a.abbr_wbm, a.is_valid, a.crte_id,
a.crte_name, a.crte_time,
b.name as outDeptName,
c.name as inDeptName
from base_item a
LEFT JOIN base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code
LEFT JOIN base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code
left join insure_item_match  iim on iim.hosp_item_id = a.id and iim.hosp_code = a.hosp_code

	a.hosp_code = #{hospCode}

		and a.is_valid = #{isValid}


		and	iim.hosp_item_id is null


		and a.is_valid = '1'


		and a.nation_code is not null and LENGTH(trim(a.nation_code))>0


		and a.id = #{id}


		and a.id in

			#{id}



		and a.code in

			#{code}



		and a.nation_code = #{nationCode}


		and a.type_code = #{typeCode}


		and a.bfc_code = #{bfcCode}


		and a.abbr = #{abbr}


		and a.spec = #{spec}


		and a.price = #{price}


		and a.one_price = #{onePrice}


		and a.two_price = #{twoPrice}


		and a.three_price = #{threePrice}


		and a.unit_code = #{unitCode}


		and a.intension = #{intension}


		and a.prompt = #{prompt}


		and a.except = #{except}


		and a.remark = #{remark}


		and a.is_out = #{isOut}


		and a.is_in = #{isIn}


		and a.is_cg = #{isCg}


		and a.is_ms = #{isMs}


		and a.is_supp_curtain = #{isSuppCurtain}


		and a.out_dept_code = #{outDeptCode}


		and a.in_dept_code = #{inDeptCode}


		and a.crte_id = #{crteId}


		and a.crte_name = #{crteName}


		and a.crte_time = #{crteTime}


		and (a.code like concat('%',#{keyword},'%')
		or a.name like concat('%',#{keyword},'%')
		or a.name_pym like concat('%',#{keyword},'%')
		or a.name_wbm like concat('%',#{keyword},'%'))


order by a.code asc

select
a.id, a.hosp_code, a.nation_code, a.type_code, a.bfc_code, a.code, a.name, a.abbr, a.spec, a.price, a.one_price,
a.two_price,a.name as itemName, a.nation_name,a.nation_name as gb_nation_name,
a.three_price, a.unit_code, a.intension, a.prompt, a.except, a.remark, a.is_out, a.is_in, a.is_cg, a.is_ms,
a.is_supp_curtain,a.other_name,
a.out_dept_code, a.in_dept_code, a.name_pym, a.name_wbm, a.abbr_pym, a.abbr_wbm, a.is_valid, a.crte_id,
a.crte_name, a.crte_time,a.is_generate_advice,
b.name as outDeptName,
c.name as inDeptName,a.reimbursement_ratio
from base_item a
LEFT JOIN base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code
LEFT JOIN base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code

	a.hosp_code = #{hospCode}

		and a.id = #{id}


		and a.nation_code = #{nationCode}


		and a.type_code = #{typeCode}


		and a.bfc_code = #{bfcCode}


		and a.abbr = #{abbr}


		and a.spec = #{spec}


		and a.price = #{price}


		and a.one_price = #{onePrice}


		and a.two_price = #{twoPrice}


		and a.three_price = #{threePrice}


		and a.unit_code = #{unitCode}


		and a.intension = #{intension}


		and a.prompt = #{prompt}


		and a.except = #{except}


		and a.remark = #{remark}


		and a.is_out = #{isOut}


		and a.is_in = #{isIn}


		and a.is_cg = #{isCg}


		and a.is_ms = #{isMs}


		and a.is_supp_curtain = #{isSuppCurtain}


		and a.out_dept_code = #{outDeptCode}


		and a.in_dept_code = #{inDeptCode}


		and a.abbr_pym = #{abbrPym}


		and a.abbr_wbm = #{abbrWbm}


		and a.is_valid = #{isValid}


		and a.crte_id = #{crteId}


		and a.crte_name = #{crteName}


		and a.crte_time = #{crteTime}


		and (a.code like concat('%',#{keyword},'%')
		or a.name like concat('%',#{keyword},'%')
		or a.name_pym like concat('%',#{keyword},'%')
		or a.name_wbm like concat('%',#{keyword},'%'))


		and a.type_code in

			#{item}



		and a.is_generate_advice = #{isAdvice}


order by a.code asc

mapper.update:
update base_item
nation_code = #{nationCode}, nation_name = #{gbNationName}, type_code = #{typeCode}, bfc_code = #{bfcCode}, code = #{code}, name = #{name}, abbr = #{abbr}, spec = #{spec}, price = #{price}, one_price = #{onePrice}, two_price = #{twoPrice}, three_price = #{threePrice}, unit_code = #{unitCode}, intension = #{intension}, prompt = #{prompt}, except = #{except}, remark = #{remark}, is_out = #{isOut}, is_in = #{isIn}, is_cg = #{isCg}, is_ms = #{isMs}, is_supp_curtain = #{isSuppCurtain}, out_dept_code = #{outDeptCode}, in_dept_code = #{inDeptCode}, name_pym = #{namePym}, name_wbm = #{nameWbm}, abbr_pym = #{abbrPym}, abbr_wbm = #{abbrWbm}, reimbursement_ratio = #{reimbursementRatio}, other_name=#{otherName}
where id = #{id} and hosp_code = #{hospCode}

update base_item set is_valid = #{isValid} where hosp_code = #{hospCode} and
id in
#{id}

mapper.select:
select count(id)
from base_item
where hosp_code = #{hospCode}

	and code = #{code}


	and nation_code = #{nationCode}


	and id != #{id}

select a.id, a.hosp_code, a.nation_code, a.type_code, a.bfc_code, a.code, a.name, a.abbr, a.spec, a.price, a.one_price, a.two_price, a.three_price, a.unit_code, a.intension, a.prompt, a.except, a.remark, a.is_out, a.is_in, a.is_cg, a.is_ms, a.is_supp_curtain, a.out_dept_code, a.in_dept_code, a.name_pym, a.name_wbm, a.abbr_pym, a.abbr_wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time,a.other_name, (select count(0) from base_advice_detail d where d.type_code = '3' and d.item_code = a.code) as adviceFlag, b.name as outDeptName, c.name as inDeptName, (select id from base_finance_classify where code=a.bfc_code and hosp_code=a.hosp_code) bfcId from base_item a LEFT JOIN base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code LEFT JOIN base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code
a.hosp_code = #{hospCode}
and a.code = #{code}

select
a.id, a.hosp_code, a.nation_code, a.type_code, a.bfc_code, a.code, a.name, a.abbr, a.spec, a.price, a.one_price,
a.two_price,
a.three_price, a.unit_code, a.intension, a.prompt, a.except, a.remark, a.is_out, a.is_in, a.is_cg, a.is_ms,
a.is_supp_curtain,a.other_name,
a.out_dept_code, a.in_dept_code, a.name_pym, a.name_wbm, a.abbr_pym, a.abbr_wbm, a.is_valid, a.crte_id,
a.crte_name, a.crte_time,
b.id as bfcId
from base_item a
LEFT JOIN base_finance_classify b on a.bfc_code = b.code

	a.hosp_code = #{hospCode}

		and a.id = #{id}


		and a.id in

			#{id}



		and a.code in

			#{code}



		and a.nation_code = #{nationCode}


		and a.type_code = #{typeCode}


		and a.bfc_code = #{bfcCode}


		and a.abbr = #{abbr}


		and a.spec = #{spec}


		and a.price = #{price}


		and a.one_price = #{onePrice}


		and a.two_price = #{twoPrice}


		and a.three_price = #{threePrice}


		and a.unit_code = #{unitCode}


		and a.intension = #{intension}


		and a.prompt = #{prompt}


		and a.except = #{except}


		and a.remark = #{remark}


		and a.is_out = #{isOut}


		and a.is_in = #{isIn}


		and a.is_cg = #{isCg}


		and a.is_ms = #{isMs}


		and a.is_supp_curtain = #{isSuppCurtain}


		and a.out_dept_code = #{outDeptCode}


		and a.in_dept_code = #{inDeptCode}


		and a.is_valid = #{isValid}


		and a.crte_id = #{crteId}


		and a.crte_name = #{crteName}


		and a.crte_time = #{crteTime}


order by a.crte_time desc

select id, hosp_code, nation_code, type_code, bfc_code, code, name, abbr, spec, price, one_price, two_price, three_price, unit_code, intension, prompt,except, remark, is_out, is_in, is_cg, is_ms, is_supp_curtain, out_dept_code, in_dept_code, name_pym, name_wbm, abbr_pym, abbr_wbm, is_valid, crte_id, crte_name, crte_time,other_name from base_item where hosp_code = #{hospCode} and
id in
#{id}
