mapper.select:
select id, hosp_code, type_code, bfc_code, code,nation_code, name, spec, price, unit_code, split_ratio, split_unit_code, split_price, is_supp_curtain, use_code, remark, prod_code, reg_cert_no, pym, wbm, is_valid, crte_id, crte_name, crte_time,last_buy_price,last_split_buy_price,avg_buy_price,avg_sell_price,model, (select count(0) from stro_stock e where e.item_code = '2' and e.item_id = base_material.id ) as stockFlag,other_name, (select name from base_product where base_product.hosp_code = #{hospCode} and prod_code = code) as prodName from base_material where id in
#{item}
and hosp_code = #{hospCode}

select bm.id, bm.hosp_code, bm.type_code, bm.bfc_code, bm.code,bm.nation_code, bm.name, bm.spec, bm.price, bm.unit_code, bm.split_ratio, bm.split_unit_code, bm.split_price, bm.is_supp_curtain, bm.use_code, bm.remark, bm.prod_code, bm.reg_cert_no, bm.pym, bm.wbm, bm.is_valid, bm.crte_id, bm.crte_name, bm.crte_time,bm.other_name, bm.last_buy_price,bm.last_split_buy_price,bm.avg_buy_price,bm.avg_sell_price,bm.model from base_material bm left join insure_item_match iim on iim.hosp_item_id = bm.id and bm.hosp_code = iim.hosp_code
bm.hosp_code = #{hospCode}
and bm.is_valid = #{isValid}
and iim.hosp_item_id is null
and bm.is_valid = '1'
and bm.nation_code is not null and LENGTH(trim(bm.nation_code))>0
and bm.id in
#{id}
and (bm.name like concat('%',#{keyword},'%') or bm.pym like concat('%',#{keyword},'%') or bm.wbm like concat('%',#{keyword},'%'))

select id, hosp_code, type_code, bfc_code, code,nation_code, name, spec, price, unit_code, split_ratio, split_unit_code, split_price, is_supp_curtain, use_code, remark, prod_code, reg_cert_no, pym, wbm, is_valid, crte_id, crte_name, crte_time,model,other_name, last_buy_price,last_split_buy_price,avg_buy_price,avg_sell_price from base_material where hosp_code = #{hospCode}
and id in
#{id}

select
  a.id, a.hosp_code, a.type_code, a.bfc_code, a.code,nation_code, a.name, a.name as itemName,
       a.spec, a.price, a.unit_code, a.split_ratio, a.split_unit_code, a.split_price,
  a.is_supp_curtain, a.use_code, a.remark, a.prod_code, a.reg_cert_no, a.pym, a.wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time,
  a.last_buy_price,a.last_split_buy_price,a.avg_buy_price,a.avg_sell_price,model,a.other_name,
  '2' as itemCode,a.reimbursement_ratio,a.outpt_use_code,a.nation_name,a.nation_name as gb_nation_name
from base_material a


        and a.id = #{id}

        and a.hosp_code = #{hospCode}

        and a.type_code = #{typeCode}


        and a.bfc_code = #{bfcCode}


        and a.code = #{code}


        and a.name = #{name}


        and a.spec = #{spec}


        and a.price = #{price}


        and a.unit_code = #{unitCode}


        and a.split_ratio = #{splitRatio}


        and a.split_unit_code = #{splitUnitCode}


        and a.split_price = #{splitPrice}


        and a.is_supp_curtain = #{isSuppCurtain}


        and a.use_code = #{useCode}


        and a.remark = #{remark}


        and a.prod_code = #{prodCode}


        and a.reg_cert_no = #{regCertNo}


        and a.is_valid = #{isValid}


        and a.crte_id = #{crteId}


        and a.crte_name = #{crteName}


        and a.crte_time = #{crteTime}


        and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))


      and a.model = #{model}


        and a.type_code in

            #{item}



order by a.crte_time desc

mapper.update:
update base_material
hosp_code = #{hospCode}, type_code = #{typeCode}, bfc_code = #{bfcCode}, code = #{code}, nation_code = #{nationCode}, name = #{name}, spec = #{spec}, price = #{price}, unit_code = #{unitCode}, last_buy_price = #{lastBuyPrice}, last_split_buy_price = #{lastSplitBuyPrice}, avg_buy_price = #{avgBuyPrice}, avg_sell_price = #{avgSellPrice}, split_ratio = #{splitRatio}, split_unit_code = #{splitUnitCode}, split_price = #{splitPrice}, is_supp_curtain = #{isSuppCurtain}, use_code = #{useCode}, remark = #{remark}, prod_code = #{prodCode}, reg_cert_no = #{regCertNo}, pym = #{pym}, wbm = #{wbm}, is_valid = #{isValid}, model = #{model}, other_name = #{otherName}, is_offical = #{isOffical}, reimbursement_ratio = #{reimbursementRatio}, outpt_use_code = #{outptUseCode}, nation_name=#{gbNationName},
where id = #{id} and hosp_code = #{hospCode}

update  base_material

    is_valid = #{isValid}

  where id in

     #{id}

and hosp_code = #{hospCode}

mapper.select:
select count(code)
from base_material
where hosp_code = #{hospCode} and code = #{code}

    and id != #{id}

SELECT a.id, a.hosp_code, a.type_code, a.bfc_code, a.code,nation_code, a.name, a.name as itemName,'2' as itemCode, a.spec, a.price, a.unit_code, a.split_ratio, a.split_unit_code, a.split_price,a.model, a.is_supp_curtain, a.use_code, a.remark, a.prod_code, a.reg_cert_no, a.pym, a.wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time, a.last_buy_price, a.last_split_buy_price, a.avg_buy_price, a.avg_sell_price,a.other_name, (select t.name from base_dept t where t.id = l.biz_id and t.hosp_code = l.hosp_code)bizName, l.biz_id , l.batch_no,l.expiry_date,l.num stockNum,l.split_num, (select d.id from base_finance_classify d where a.bfc_code = d.code and a.hosp_code = d.hosp_code limit 1) bfcId, (select d.name from base_finance_classify d where a.bfc_code = d.code and a.hosp_code = d.hosp_code limit 1) bfcName FROM stro_stock k, stro_stock_detail l, base_material a WHERE k.hosp_code = l.hosp_code AND k.biz_id = l.biz_id AND k.item_id = l.item_id AND l.item_id = a.id AND l.hosp_code = a.hosp_code AND k.item_code = '2'
AND l.biz_id = #{bizId}
AND l.hosp_code = #{hospCode}
and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))
and l.num > 0

SELECT a.hosp_code, a.id, max(a.NAME) AS itemName, '2' AS itemCode, max(a.price) price, max(a.last_buy_price) last_buy_price, sum(l.num) stockNum, max(a.unit_code) unit_code, max(a.split_ratio) split_ratio, sum(l.split_num) split_num, max(a.split_unit_code) split_unit_code, max(a.last_split_buy_price) last_split_buy_price, max(a.split_price) split_price, max(a.spec) spec, max(a.model) model, (select bp.name from base_product bp where bp.code = max(a.prod_code) and bp.hosp_code = a.hosp_code) as prodName FROM stro_stock k, stro_stock_detail l, base_material a WHERE k.hosp_code = l.hosp_code AND k.biz_id = l.biz_id AND k.item_id = l.item_id AND l.item_id = a.id AND l.hosp_code = a.hosp_code AND k.item_code = '2'
AND l.biz_id = #{bizId}
AND l.hosp_code = #{hospCode}
and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))
and l.num > 0
GROUP BY a.hosp_code, a.id
