mapper.update:
update base_nurse_order
set is_valid = #{isValid}
where hosp_code = #{hospCode} and id = #{id}

mapper.select:
select count(1)
from base_nurse_order
where hosp_code = #{hospCode}
and code = #{code}
and is_valid = '1'

    and id != #{id}

select *
from base_nurse_tbhead
where is_valid = '1'
  and hosp_code = #{hospCode}
  and bno_code = #{bnoCode}
order by seq_no
