mapper.select:
select count(1)
from base_nurse_tbhead
where hosp_code = #{hospCode}
and code = #{code}
and is_valid = '1'
and bno_code = #{bnoCode}

    and id != #{id}

select *
from base_nurse_tbhead
where hosp_code = #{hospCode}
  and id = #{id}

update.foreach:
update base_nurse_tbhead
set is_valid = '0'
where hosp_code = #{item.hospCode}
and id = #{item.id}

mapper.select:
select MAX(seq_no)
from base_nurse_tbhead
where hosp_code = #{hospCode}
  and is_valid = '1'
  and bno_code = #{bnoCode}

SELECT
COUNT(item_code)
FROM
base_nurse_tbhead
WHERE
hosp_code = #{hospCode}
AND bno_code = #{bnoCode}
AND is_valid = '1'
and item_code = #{itemCode}

    and id != #{id}
