select id, hosp_code, in_profile, out_profile, name, gender_code, age,age_unit_code, birthday, nation_code, nationality_cation, native_place, education_code, occupation_code, marry_code, cert_code, cert_no, phone, email, often_code, now_prov, now_city, now_area, now_address, now_post_code, native_prov, native_city, native_area, native_address, native_post_code, contact_name, contact_rela_code, contact_phone, contact_post_code, contact_address, take_name, take_rela_code, take_cert_no, take_phone, work, work_phone, work_post_code, work_address, blood_code, family_disease, expose_disease, heredity_disease, allergy_disease, past_disease, past_oper, past_trauma, past_blood, past_remark, allergy_remark, source_tj_code, source_tj_remark, pym, wbm, total_out, total_in, outpt_last_visit_time, inpt_last_visit_time, remark, patient_code, preferential_type_id, is_valid, crte_id, crte_name, crte_time, (select name from base_preferential_type bpt where bpt.hosp_code = a.hosp_code and bpt.id = a.preferential_type_id) as preferential_type_name from base_profile_file a where hosp_code = #{hospCode}
and is_valid = #{isValid}
and (name like concat('%', #{keyword}, '%') or pym like concat('%', #{keyword}, '%') or wbm like concat('%', #{keyword}, '%') or cert_no like concat('%', #{keyword}, '%') or phone like concat('%', #{keyword}, '%'))
and cert_code = #{certCode}
and a.id in
#{id}
order by a.crte_time desc

select id, hosp_code, in_profile, out_profile, name, gender_code, age, birthday, nation_code, nationality_cation, native_place, education_code,
       occupation_code, marry_code, cert_code, cert_no, phone, email, often_code, now_prov, now_city, now_area, now_address, now_post_code, native_prov,
       native_city, native_area, native_address, native_post_code, contact_name, contact_rela_code, contact_phone, contact_post_code, contact_address,
       take_name, take_rela_code, take_cert_no, take_phone, work, work_phone, work_post_code, work_address, blood_code, family_disease, expose_disease,
       heredity_disease, allergy_disease, past_disease, past_oper, past_trauma, past_blood, past_remark, allergy_remark, source_tj_code, source_tj_remark,
       pym, wbm, total_out, total_in, outpt_last_visit_time, inpt_last_visit_time, patient_code, preferential_type_id, remark, is_valid, crte_id, crte_name,
       crte_time, age_unit_code
from base_profile_file
where hosp_code = #{hospCode}

    and id = #{id}


    and cert_code = #{certCode}


    and cert_no = #{certNo}

mapper.delete:
delete from base_profile_file
where hosp_code = #{hospCode}
and id = #{id}

mapper.select:
select id, hosp_code, in_profile, out_profile, name, gender_code, age, birthday, nation_code, nationality_cation, native_place, education_code, occupation_code, marry_code, cert_code, cert_no, phone, email, often_code, now_prov, now_city, now_area, now_address, now_post_code, native_prov, native_city, native_area, native_address, native_post_code, contact_name, contact_rela_code, contact_phone, contact_post_code, contact_address, take_name, take_rela_code, take_cert_no, take_phone, work, work_phone, work_post_code, work_address, blood_code, family_disease, expose_disease, heredity_disease, allergy_disease, past_disease, past_oper, past_trauma, past_blood, past_remark, allergy_remark, source_tj_code, source_tj_remark, pym, wbm, total_out, total_in, outpt_last_visit_time, inpt_last_visit_time, remark, patient_code, preferential_type_id, is_valid, crte_id, crte_name, crte_time,age_unit_code from base_profile_file where cert_no = #{certNo}
and cert_code = #{certCode}
and id !=#{id}
order by crte_time desc
