b.name as deptName
from sys_user a
left join base_dept b on a.dept_code = b.code and a.hosp_code = b.hosp_code

	a.hosp_code = #{hospCode}

		and a.id = #{id}


		and a.id in

			#{item}



		and a.work_type_code in

			#{workTypeCode}



		and a.dept_code = #{deptCode}


