SELECT
	c.*
FROM
	sys_user a,
	sys_user_system b,
	sys_user c
WHERE
	a.code = b.user_code
	AND a.hosp_code = b.hosp_code
	AND b.teacher_code = c.code
	AND b.hosp_code = c.hosp_code
	AND a.hosp_code = #{hospCode}
	and b.dept_code = #{deptCode}
	and b.system_code =#{systemCode}
	AND a.code = #{userCode}

		and c.code = #{code}


		and c.password = #{password}

SELECT a.id, a.hosp_code, a.dept_code, a.CODE, a.PASSWORD, a.is_in_job, a.NAME, a.age, a.birthday, a.gender_code, a.nation_code, a.native_place, a.marry_code, a.is_pm, a.in_pm_date, a.cert_code, a.cert_no, a.photo_image, a.sign_image, a.introduce, a.speciality, a.address, a.email, a.phone, a.education_code, a.major_code, a.school, a.school_date, a.school_image, a.work_date, a.work_type_code, a.duties_code, a.prac_certi_no, a.prac_certi_name, a.durg_code, a.antibacterial_code, a.ph_code, a.opeartion_code, a.pswd_err_cnt, a.last_login_time, a.last_login_ip, a.status_code, a.pym, a.wbm, a.crte_id, a.crte_name, a.crte_time, a.signature_path, a.doctor_intro, a.is_guide, a.is_anaesthesia, b.NAME AS deptName FROM sys_user a left join sys_user_system c on c.user_code = a.code LEFT JOIN base_dept b ON c.dept_code = b.CODE AND a.hosp_code = b.hosp_code WHERE a.hosp_code = #{hospCode}
AND a.status_code = #{statusCode}
AND a.work_type_code LIKE CONCAT(#{workTypeCode}, '%' )
AND b.type_code = #{deptTypeCode}
GROUP BY a.id,a.code
